<?php

if ($_SERVER['REQUEST_METHOD'] === 'POST') {
    $username = $_POST['username'];
    $password = $_POST['password'];

    // Database connection
    $conn = new mysqli('localhost', 'root', '', 'kalpanapurdb');
    if ($conn->connect_error) {
        die('Database connection failed: ' . $conn->connect_error);
    }

    // Query to fetch user details
    $stmt = $conn->prepare('SELECT userid, password FROM users WHERE username = ?');
    $stmt->bind_param('s', $username);
    $stmt->execute();
    $stmt->store_result();
    $stmt->bind_result($userid, $hashedPassword);

    if ($stmt->fetch() && password_verify($password, $hashedPassword)) {
        // Set session for the logged-in user
        $_SESSION['user_id'] = $userid;
        header('Location: index.php'); // Redirect to home page
        exit;
    } else {
        $error = 'Invalid username or password.';
    }

    $stmt->close();
    $conn->close();
}
?>

<div class="login-container">
    <div class="login-header">
        <img src="data:image/jpeg;base64," alt="Kalpanapur Banner" class="login-banner">
        <h2>Welcome to Kalpanapur</h2>
    </div>

    <?php if (isset($error)): ?>
        <p class="error-message"><?= htmlspecialchars($error) ?></p>
    <?php endif; ?>

    <form method="POST" class="login-form">
        <label>Username:</label>
        <input type="text" name="username" required>
        <label>Password:</label>
        <input type="password" name="password" required>
        <button type="submit">Login</button>
    </form>

    <p>Don't have an account? <a href="?page=register">Register here</a>.</p>
</div>
